import ReactDOM from 'react-dom';

class MultiInput extends Component {

    componentDidMount = () => {
        let input = document.querySelector(".input");
        input.addEventListener('keyup', this.createNewInput);
    }

    createNewInput = () => {

        document.getElementById("wrapper").push

        ReactDOM.createPortal(<input type="text" className="input" />, document.getElementById("wrapper"));
    }

    render() {
        return (
            <form>
                <h4>Test</h4>


                <label>test attribute</label>
                <input type="text" className="input"/>
            </form>
        );
    }
}

export default MultiInput;
