import React, { Component } from 'react';

class MultiInput extends Component {

    constructor() {
        super();

        this.state = {
            inputs: [],
        };
    }

    handleSubmit = (e) => { e.preventDefault() }

    handleChange = (e) => {
        let inputs = [...this.state.inputs];

        inputs[e.target.dataset.id] = e.target.value;

        this.setState({inputs});
    }

    handleInputTyping = (e) => {

        let index = parseInt(e.target.getAttribute("data-id"));

        this.setState((prevState) => ({
            inputs: [...prevState.inputs, ""]
        }));

    }

    render() {
        return (
            <form onSubmit={this.handleSubmit} onChange={this.handleChange}>
                <h4>Test</h4> 
                {
                    this.state.inputs.map((value, idx) => {

                        idx += 1;

                        return(
                            <div className="form-fields" key={idx}>
                                <label>test attribute</label>
                                <input type="text" className="input" data-id={idx} onKeyDown={this.handleInputTyping} />
                                <button onClick={this.removeInput}>X</button>
                            </div>
                        )
                    })
                }
                <div className="form-fields">
                    <label>test attribute</label>
                    <input type="text" className="input" onKeyUp={this.handleInputTyping} />
                </div>


            </form>
        );
    }
}

export default MultiInput;
