import React, { Component } from 'react';

class MultiInput extends Component {

    constructor(props) {
        super(props);
    }

    handleSubmit = (e) => { e.preventDefault() }

    handleChange = (e) => {
        let inputs = [...this.props.inputs];
        inputs[e.target.dataset.id] = e.target.value;
        this.props = {inputs};

        console.log(this.props.inputs);
    }

    handleInputTyping = (e) => {

        let index = e.target.dataset.id;

        if(this.props.inputs.length <= index) {
            let inputs = [...this.props.inputs];
            inputs[e.target.dataset.id] = "";
            this.props = {inputs};
        }
    }

    removeInput = (e) => {

    }

    render() {
        return (
            <form onSubmit={this.handleSubmit} onChange={this.handleChange}>
                <h4>Test</h4>
                <div id="field-list">
                    <div className="form-fields">
                        <label>test attribute</label>
                        <input type="text" className="first input" data-id="0" onKeyUp={this.handleInputTyping} />
                        <button onClick={this.removeInput}>X</button>
                    </div>
                    {
                        this.props.inputs.map((value, idx) => {
                            console.log(value);
                            idx++;

                            return(
                                <div className="form-fields" key={idx}>
                                    <label>test attribute</label>
                                    <input type="text" className="input" data-id={idx} value={value} onKeyDown={this.handleInputTyping} />

                                    { value ? (<button className="exclude-button-invisible" onClick={this.removeInput}>X</button>) : "" }
                                </div>
                            )
                        })
                    }
                </div>
                <button>Cancel</button>
                <button>Save</button>
            </form>
        );
    }
}

export default MultiInput;
