import React, { Component } from 'react';
import SingleInput from './SingleInput';

class MultiInput extends Component {

    handleChange = (e) => {

    }

    handleSubmit = (e) => { e.preventDefault() }

    handleInputTyping = (e) => {

        let index = parseInt(e.target.getAttribute("data-id"));
        let value = e.target.value;

        if(this.state.inputs.length <= index) {
            this.setState((prevState) => ({
                inputs: [...prevState.inputs, value]
            }));
        } else {
            setTimeout(() => {
                this.state.inputs[index] = value;
            }, 5000);
        }

    }

    removeInput = (e) => {
        let index = parseInt(e.target.getAttribute("data-id"));

        if(this.state.inputs.indexOf(index) >= -1) {
            this.setState({
                inputs: this.state.inputs.splice(index, 1)
            });
        }

        console.log(this.state.inputs);
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit} onChange={this.handleChange}>
                <h4>Test</h4>
                <div className="form-fields">
                    <label>test attribute</label>
                    <input type="text" className="input" data-id="0" onKeyUp={this.handleInputTyping} />
                </div>


            </form>
        );
    }
}

export default MultiInput;
