import React, { Component } from 'react';
import PropTypes from 'prop-types'

export default class MultiInput extends Component {

    constructor(props) {
        super(props);

        //even if the argument is an empty array, forces it to render the forms
        if(this.props.inputs.length == 0) { this.props.inputs.push("") };

        this.state = {
            inputs: this.props,
        };
    }

    handleSubmit = (e) => { e.preventDefault() }

    handleChange = (e) => {
        let inputs = [...this.state.inputs];

        inputs[e.target.dataset.id] = e.target.value;

        this.setState({inputs}, console.log(this.state.inputs));
    }

    handleInputTyping = (e) => {

        let index = e.target.dataset.id;

        if(this.state.inputs.length <= index) {
            this.setState((prevState) => ({
                inputs: [...prevState.inputs, ""]
            }));
        }
    }

    render() {

        return (
            <form onSubmit={this.handleSubmit} onChange={this.handleChange}>
                <h4>Test</h4>
                <div id="field-list">
                {
                    this.state.inputs.map((value, idx) => {
                        return(
                            <div className="form-fields" key={idx}>
                                <label>test attribute</label>
                                <input type="text" className="input" data-id={idx} defaultValue={this.props.inputs[idx]} onKeyDown={this.handleInputTyping} />
                                { value ? (<button className="exclude-button-invisible" onClick={this.removeInput}>X</button>) : "" }
                            </div>
                            )
                        })
                }
                </div>
                <button>Cancel</button>
                <button>Save</button>
            </form>
        );
    }
}

MultiInput.propTypes = {
    inputs: PropTypes.array.isRequired,
}
