import React, { Component } from 'react';

class FormInput extends Component {

    constructor(props) {
        super(props);
    }

    render() {

        return(
            props.inputs.map((val, idx) => {
                return (
                    <div className="form-fields" key={this.props.idx}>
                        <label>test attribute</label>
                        <input type="text" className="input" data-id={this.props.idx} value={this.props.value} />
                        <button onClick={this.removeInput}>X</button>
                    </div>
                )
            })
        )
    }
}

export default FormInput;
