import React, { Component } from 'react';

class MultiInput extends Component {

    constructor() {
        super();

        this.state = {
            inputs: [""],
        };
    }

    handleChange = (e) => {

    }

    handleSubmit = (e) => { e.preventDefault() }

    addInput = (e) => {

        //alter this
        let index = parseInt(e.target.getAttribute("data-id")) + 1;

        if(this.state.inputs.length <= index) {
            let value = e.target.value;

            this.setState((prevState) => ({
                inputs: [...prevState.inputs, value]
            }));
        }
    }

    removeInput = (e) => {
        let index = parseInt(e.target.getAttribute("data-id"));

        if(this.state.inputs.indexOf(index) > -1) {
            this.state.inputs.splice(index, 1);
        }
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit} onChange={this.handleChange}>
                <h4>Test</h4>
                {
                    this.state.inputs.map((value, idx) => {
                        return(
                            <div className="form-fields" key={idx}>
                                <label>test attribute</label>
                                <input type="text" className="input" data-id={idx} onKeyPress={this.addInput} />
                                <button>X</button>
                            </div>
                        )
                    })
                }

            </form>
        );
    }
}

export default MultiInput;
