import React, { Component } from 'react';
import FormInput from './FormInput'
import PropTypes from 'prop-types';

export default class MultiInput extends Component {

    constructor(props) {
        super(props);

        if(props.inputs.length === 0) { props.inputs.push("") }

        this.state = {
            inputs: props.inputs,
        };
    }

    handleSubmit = (e) => { e.preventDefault() }

    handleChange = (e) => {
        let inputs = [...this.state.inputs];
        inputs[e.target.dataset.id] = e.target.value;
        this.setState({inputs}, console.log(this.state.inputs));
    }

    handleInputTyping = (e) => {

        let index = e.target.dataset.id;

        if(this.state.inputs.length <= index) {
            this.setState((prevState) => ({
                inputs: [...prevState.inputs, ""]
            }));
        }
    }

    removeInput = (e) => {

    }

    render() {
        return (
            <form onSubmit={this.handleSubmit} onChange={this.handleChange}>
                <h4>Test</h4>
                <div id="field-list">
                {
                    this.state.inputs.map((value, idx) => {
                        return(<FormInput idx={idx} value={value} handleInputTyping={this.handleInputTyping} key={idx} />)
                    })
                }
                </div>
                <button>Cancel</button>
                <button>Save</button>
            </form>
        );
    }
}

MultiInput.propTypes = {
    inputs: PropTypes.array,
}
